Get-VMHost | Get-VMHostNetwork | Select Hostname, VMkernelGateway -ExpandProperty VirtualNic | Where {$_.VMotionEnabled} | Select Hostname, PortGroupName, IP, SubnetMask, @{Name="Gateway";Expression={$_.ExtensionData.spec.IpRouteSpec.IpRouteConfig.DefaultGateway}}, Devicename,@{Name="NetStack";Expression={$_.ExtensionData.spec.NetstackInstanceKey}} | Sort-Object -Property IP | format-table